mapper.select:
select
  id, kind_id, kind_name, operateor
from sys_object_kind


        and id = #{id}


        and kind_id = #{kindId}


        and kind_name = #{kindName}


        and operateor = #{operateor}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from sys_object_kind


        and id = #{id}


        and kind_id = #{kindId}


        and kind_name = #{kindName}


        and operateor = #{operateor}
